#ifndef FE310_VENDOR_UART_H
#define FE310_VENDOR_UART_H

#include "bits.h"

#define UART0_BASE 0x10013000
#define UART1_BASE 0x10023000

#define UART_TXDATA_DATA GENMASK(7, 0)
#define UART_TXDATA_FULL BITMASK(31)
#define UART_RXDATA_DATA GENMASK(7, 0)
#define UART_RXDATA_EMPTY BITMASK(31)
#define UART_TXCTRL_TXEN BITMASK(0)
#define UART_TXCTRL_NSTOP BITMASK(1)
#define UART_TXCTRL_TXCNT GENMASK(18, 16)
#define UART_RXCTRL_RXEN BITMASK(0)
#define UART_RXCTRL_RXCNT GENMASK(18, 16)
#define UART_IE_TXWM BITMASK(0)
#define UART_IE_RXWM BITMASK(1)
#define UART_IP_TXWM BITMASK(0)
#define UART_IP_RXWM BITMASK(1)
#define UART_DIV_DIV GENMASK(15, 0)

#endif /* FE310_VENDOR_UART_H */
